    public static int[] solve(int[] A) {
        Arrays.sort(A);
        int mod = 1000000007;
        int i = 0;
        int j = A.length;
        long max = 0;
        long min = 0;
        while (i < (A.length / 2) && j >= 0) {
            max = max + Math.abs(A[i] - A[j - 1]);
            i++;
            j--;
        }
        i = 0;
        while (i < A.length) {
            min = min + Math.abs(A[i + 1] - A[i]);
            i += 2;
        }
        return new int[]{(int) (max % mod), (int) (min % mod)};
    }
