package dc.clazz.String.Homework;

import java.util.HashSet;
import java.util.Set;

public class AmazingSubarrays {
    public static void main(String args[]) {
        String str = "";
        System.out.println(getSolve(str));
    }

    public static int getSolve(String A) {
        Set<Character> vowels = new HashSet<>();
        vowels.add('a');
        vowels.add('e');
        vowels.add('i');
        vowels.add('o');
        vowels.add('u');
        vowels.add('A');
        vowels.add('E');
        vowels.add('I');
        vowels.add('O');
        vowels.add('U');
        int count = 0;
        for (int i = 0; i < A.length(); i++) {
            if (vowels.contains(A.charAt(i))) {
                count += A.length() - i;
                count %= (10003);
            }
        }
        return count;

    }
}
